public class BookSorter {
    public static void main(String[] args) {
        List<Book> books = new ArrayList<>();
        books.add(new Book(101, "Java", 350, 250));
        books.add(new Book(102, "Data Structures", 450, 300));
        books.add(new Book(103, "Algorithms", 400, 280));
        books.add(new Book(104, "Spring", 300, 200));
        books.add(new Book(105, "MicroServices", 500, 350));

        Collections.sort(books, Comparator.comparingInt(b -> b.id));
        System.out.println("Ascending by Book ID: " + books);

        Collections.sort(books, (b1, b2) -> b2.name.compareTo(b1.name));
        System.out.println("\nDescending by Book Name: " + books);

        Collections.sort(books, Comparator.comparingDouble(b -> b.price));
        System.out.println("\nAscending by Book Price: " + books);

        Collections.sort(books, (b1, b2) -> b2.pages - b1.pages);
        System.out.println("\nDescending by Number of Pages: " + books);
    }
}

