public class RomanConverter {
    public static void main(String[] args) {
        Map<Character, Integer> romanMap = new HashMap<>();
        romanMap.put('I', 1); romanMap.put('V', 5); romanMap.put('X', 10);
        romanMap.put('L', 50); romanMap.put('C', 100); romanMap.put('D', 500);
        romanMap.put('M', 1000);

        String[] inputs = {"XV", "XXV", "VII", "IX"};
        int[] targets = {15, 25, 7, 9};

        for (int i = 0; i < inputs.length; i++) {
            System.out.println("Roman Numeral: " + inputs[i] + " -> Target: " + targets[i] + " -> Computed Value: " + romanToInt(inputs[i], romanMap));
        }
    }

    public static int romanToInt(String s, Map<Character, Integer> map) {
        int total = 0, prev = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            int curr = map.get(s.charAt(i));
            if (curr < prev) total -= curr;
            else total += curr;
            prev = curr;
        }
        return total;
    }
}

