import java.util.Scanner;

class Employee {
    int id;
    String name;
    double salary;

    Employee(int id, String name, double salary) {
        this.id = id;
        this.name = name;
        this.salary = salary;
    }
}

class EmployeeRepository {
    void printEmployee(Employee emp) {
        System.out.println("Employee Details:");
        System.out.println("ID: " + emp.id);
        System.out.println("Name: " + emp.name);
        System.out.println("Salary: " + emp.salary);
    }
}

class EmployeeService {
    void processEmployee(Employee emp) {
        EmployeeRepository repo = new EmployeeRepository();
        repo.printEmployee(emp);
    }
}

class EmployeeController {
    void handleEmployee(Employee emp) {
        EmployeeService service = new EmployeeService();
        service.processEmployee(emp);
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        System.out.print("Enter Employee ID: ");
        int id = sc.nextInt();
        sc.nextLine();

        System.out.print("Enter Employee Name: ");
        String name = sc.nextLine();

        System.out.print("Enter Employee Salary: ");
        double salary = sc.nextDouble();

        Employee emp = new Employee(id, name, salary);
        EmployeeController controller = new EmployeeController();
        controller.handleEmployee(emp);
    }
}

import java.util.Scanner;

class Employee {
    int id;
    String name;
    double salary;

    Employee(int id, String name, double salary) {
        this.id = id;
        this.name = name;
        this.salary = salary;
    }
}

class EmployeeRepository {
    Employee createEmployee() {
        Scanner sc = new Scanner(System.in);
        System.out.print("Enter Employee ID: ");
        int id = sc.nextInt();
        sc.nextLine(); // consume newline
        System.out.print("Enter Employee Name: ");
        String name = sc.nextLine();
        System.out.print("Enter Employee Salary: ");
        double salary = sc.nextDouble();
        return new Employee(id, name, salary);
    }
}

class EmployeeService {
    Employee getEmployee() {
        EmployeeRepository repo = new EmployeeRepository();
        return repo.createEmployee();
    }
}

class EmployeeController {
    Employee fetchEmployee() {
        EmployeeService service = new EmployeeService();
        return service.getEmployee();
    }
}

public class Main {
    public static void main(String[] args) {
        EmployeeController controller = new EmployeeController();
        Employee emp = controller.fetchEmployee();

        System.out.println("Employee Details:");
        System.out.println("ID: " + emp.id);
        System.out.println("Name: " + emp.name);
        System.out.println("Salary: " + emp.salary);
    }
}

