const readline = require('readline');
const rl = readline.createInterface({ input: process.stdin, output: process.stdout });

rl.question('', (input1) => {
    rl.question('', (input2) => {
        let a = parseInt(input1);
        let b = parseInt(input2);
        let sum = a + b;
        console.log(sum);
        rl.close();
    });
});
