public class DeleteMiddle {

    // Recursive function to delete middle element
    public static void deleteMiddle(Stack<Integer> stack, int current, int size) {
        if (stack.isEmpty() || current == size) return;

        int top = stack.pop();
        deleteMiddle(stack, current + 1, size);

        // Push back all elements except the middle one
        if (current != size / 2) {
            stack.push(top);
        }
    }

    public static void main(String[] args) {
        Stack<Integer> stack = new Stack<>();
        stack.push(1);
        stack.push(2);
        stack.push(3);
        stack.push(4);
        stack.push(5);

        System.out.println("Original Stack: " + stack);
        deleteMiddle(stack, 0, stack.size());
        System.out.println("Stack after deleting middle: " + stack);
    }
}

